mapper.select:
SELECT id FROM reviewResults 
WHERE major_code = #{majorCode} 
AND minor_code = #{minorCode} 
AND project_phase = #{projectPhase} 
AND supplier = #{supplier}
AND problem_point = #{problemPoint}

    AND version = #{version}


    AND (version IS NULL OR version = '')

SELECT * FROM reviewResults WHERE id IN

    #{id}

ORDER BY created_time ASC

mapper.delete:
DELETE FROM reviewResults


