        AND problem_tag1 = #{filters.problemTag1}


        AND problem_tag2 = #{filters.problemTag2}


        AND prevention_notes LIKE CONCAT('%', #{filters.preventionNotes}, '%')

mapper.select:
SELECT * FROM reviewResults


        AND test_date >= #{filters.testDateFrom}


        AND test_date <= #{filters.testDateTo}


        AND major_code LIKE CONCAT('%', #{filters.majorCode}, '%')


        AND minor_code LIKE CONCAT('%', #{filters.minorCode}, '%')


        AND project_phase = #{filters.projectPhase}


        AND version LIKE CONCAT('%', #{filters.version}, '%')


        AND problem_process = #{filters.problemProcess}


        AND problem_level = #{filters.problemLevel}


        AND development_method = #{filters.developmentMethod}


        AND supplier LIKE CONCAT('%', #{filters.supplier}, '%')


        AND solution_provider LIKE CONCAT('%', #{filters.solutionProvider}, '%')


        AND problem_point LIKE CONCAT('%', #{filters.problemPoint}, '%')


        AND problem_reason LIKE CONCAT('%', #{filters.problemReason}, '%')


        AND improvement_measures LIKE CONCAT('%', #{filters.improvementMeasures}, '%')


        AND is_preventable = #{filters.isPreventable}


        AND responsible_department = #{filters.responsibleDepartment}


        AND problem_status = #{filters.problemStatus}


        AND problem_tag1 = #{filters.problemTag1}


        AND problem_tag2 = #{filters.problemTag2}


        AND prevention_notes LIKE CONCAT('%', #{filters.preventionNotes}, '%')


ORDER BY created_time ASC
