mapper.update:
UPDATE `groups`
SET is_displayed = #{is_displayed}, display_order = #{display_order}
WHERE id = #{id}

mapper.select:
SELECT cr.id, cr.id as change_record_id, cr.electric_sample_id as sample_id, cr.tester, cr.start_date as schedule_start_date, cr.end_date as schedule_end_date, cr.update_time, cr.schedule_color, cr.is_used, cr.remark, ei.sample_model, ei.sample_leader, ei.sample_sender, ei.scheduleDays, ei.isCancel as is_cancel, ei.cancel_reason FROM change_records cr LEFT JOIN electric_info ei ON cr.electric_sample_id = ei.sample_id
AND cr.tester LIKE CONCAT('%', #{tester}, '%')
AND cr.is_used = #{isUsed}
AND cr.remark LIKE CONCAT('%', #{remark}, '%')
AND cr.start_date >= #{startDate}
AND cr.end_date <= #{endDate}
ORDER BY cr.update_time DESC LIMIT 100
